definition.script:
pipeline {
    agent any

    stages {
        stage('Install Maven') {
            steps {
                sh '''
                    cd $WORKSPACE
                    wget --no-check-certificate https://archive.apache.org/dist/maven/maven-3/3.8.8/binaries/apache-maven-3.8.8-bin.tar.gz
                    tar -xzf apache-maven-3.8.8-bin.tar.gz
                    rm apache-maven-3.8.8-bin.tar.gz
                '''
            }
        }
        stage('Build') {
            steps {
                git 'https://github.com/vaiz1982/my_Maven_Project_1.2.git'
                sh '$WORKSPACE/apache-maven-3.8.8/bin/mvn -Dmaven.test.failure.ignore=true clean package'
            }
            post {
                success {
                    // Skip junit since there are no test results
                    // Just archive the artifact
                    archiveArtifacts 'target/*.jar'
                    echo 'Build successful! Artifact archived.'
                }
            }
        }
    }
}
